import React, { Component } from 'react'

export default class Like extends Component {

     state = {
          likes: 0,
          updated: false
     }
     updateLikes = () => {
          if (!this.state.updated) {
               this.setState((prevState, props) => {
                    return {
                         likes: prevState.likes + 1,
                         updated: true
                    }
               })
          } else {
               this.setState((prevState, props) => {
                    return {
                         likes: prevState.likes - 1,
                         updated: false
                    }
               })
          }
     }
     render() {
          return (
               <div className='like-btn'><button onClick={this.updateLikes}>Like</button>
                    <p>{this.state.likes}</p></div>)
     }
}
